package variables;

public class Sample2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//		Addition of 2 numbers
		int n1 = 20;
		int n2 = 30;
		int res = n1 + n2;
		System.out.println("Sum of two numbers: " + res);
		
//		Subtraction of 2 numbers
		int n3 = 40;
		int n4 = 10;
		int res1 = n3 - n4;
		System.out.println("Subtraction of two numbers: " + res1);
		
//		Multiplication of 2 numbers
		int n5 = 10;
		int n6 = 5;
		int res2 = n5 * n6;
		System.out.println("Multiplication of two numbers: " + res2);
		
//		Division of 2 numbers
		int n7 = 18;
		int n8 = 3;
		int res3 = n7 / n8;
		System.out.println("Division of two numbers: " + res3);

	}

}
